import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  template: `
    <main class="app">
      <section class="card">
        <h1>Counter</h1>
        <div class="value">{{ count }}</div>
        <div class="actions">
          <button (click)="dec()" [disabled]="isZero" aria-label="decrement">−</button>
          <button (click)="inc()" aria-label="increment">+</button>
          <button (click)="reset()" class="ghost" aria-label="reset">Reset</button>
        </div>
      </section>
    </main>
  `,
  styles: [
    `.app{min-height:100vh;display:grid;place-items:center;background:#0a0a0a;color:#e5e7eb;}`,
    `.card{padding:16px 20px;background:#0b1020;border-radius:12px;border:1px solid #1f2937;display:grid;gap:12px;}`,
    `.value{font-size:40px;font-weight:800;text-align:center;}`,
    `.actions{display:flex;gap:8px;justify-content:center;}`,
    `button{padding:8px 14px;border-radius:10px;border:none;cursor:pointer;font-weight:600;}`,
    `button[disabled]{opacity:.5;cursor:not-allowed;}`,
    `.ghost{background:transparent;color:#e5e7eb;border:1px solid #374151;}`
  ]
})
export class AppComponent {
  count = 0;

  get isZero(): boolean {
    return this.count === 0;
  }

  inc(): void {
    this.count++;
  }

  dec(): void {
    if (!this.isZero) {
      this.count--;
    }
  }

  reset(): void {
    this.count = 0;
  }
}
