#thanks:target{display:block;}

body { font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; margin: 2rem; line-height: 1.6; }
form { display: flex; flex-direction: column; gap: 0.75rem; max-width: 340px; }
label { font-weight: 500; }
input { padding: 0.4rem 0.5rem; border: 1px solid #ccc; border-radius: 4px; }
button { padding: 0.5rem; background: #007acc; color: white; border: none; border-radius: 4px; cursor: pointer; }
button:hover { background: #005fa3; }
#status { font-weight: 600; margin-top: 0.25rem; }

