// Handling errors: async/await
async function load() {
  try {
    const data = await fetch('/api').then(r => {
      if (!r.ok) throw new Error('HTTP ' + r.status);
      return r.json();
    });
    return data;
  } catch (e) {
    // handle or rethrow
    throw e;
  }
}

