    }
  ];

  // When false: only one item should be open at a time
  // When true: multiple items can be open
  allowMultiple = false;

  // TODO: track which indexes are currently open
  // Hint: this can be an array of numbers (indexes)
  openIndexes: number[] = [];

  // TODO: return true if the item at this index is open
  isItemOpen(index: number): boolean {
    return false;
  }

  // TODO: toggle the item at this index
  // - In single-open mode, only this index should be open
  // - In multi-open mode, treat openIndexes like a set
  onItemToggle(index: number): void {
  }

  // Optional: when switching modes, you may want to adjust openIndexes
  onToggleAllowMultiple(): void {
    this.allowMultiple = !this.allowMultiple;
    // TODO (optional): normalize openIndexes when switching from multi → single
  }
}

