/src/app/app.component.css.code:
.app-shell {
  min-height: 100vh;
  background: #f9fafb;
  color: #020617;
  display: flex;
  flex-direction: column;
  padding: 16px;
  box-sizing: border-box;
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
}

.app-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 24px;
}

.app-title {
  margin: 0;
  font-size: 18px;
  font-weight: 700;
}

.content {
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: flex-start;
}

.card {
  width: 100%;
  max-width: 480px;
  background: #ffffff;
  border-radius: 12px;
  border: 1px solid #e5e7eb;
  padding: 20px;
  box-shadow: 0 18px 45px rgba(15, 23, 42, 0.35);
}

.card h2 {
  margin-top: 0;
  margin-bottom: 8px;
  font-size: 16px;
  font-weight: 700;
}

.card p {
  margin: 0;
  font-size: 14px;
  line-height: 1.5;
}

.theme-toggle {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 6px 12px;
  border-radius: 999px;
  border: 1px solid #e5e7eb;
  background: #eef2ff;
  color: #020617;
  cursor: pointer;
  font-size: 13px;
  font-weight: 600;
  transition: transform 120ms ease, box-shadow 120ms ease;
}

.theme-toggle .icon {
  font-size: 16px;
}

.theme-toggle:hover {
  transform: translateY(-1px);
  box-shadow: 0 10px 25px rgba(15, 23, 42, 0.3);
}

:host-context([data-theme='dark']) .app-shell {
  background: #020617;
  color: #e5e7eb;
}

:host-context([data-theme='dark']) .card {
  background: #020617;
  border-color: #1f2937;
  box-shadow: 0 18px 45px rgba(15, 23, 42, 0.75);
}

:host-context([data-theme='dark']) .theme-toggle {
  border-color: #1f2937;
  background: #111827;
  color: #e5e7eb;
}

