:root {
  color-scheme: light;
  --bg: #f9fafb;
  --fg: #020617;
  --card-bg: #ffffff;
  --border: #e5e7eb;
  --accent: #4f46e5;
  --accent-soft: #eef2ff;
}

:root[data-theme='dark'] {
  color-scheme: dark;
  --bg: #020617;
  --fg: #e5e7eb;
  --card-bg: #020617;
  --border: #1f2937;
  --accent: #a855f7;
  --accent-soft: #111827;
}

html, body {
  margin: 0;
  height: 100%;
}

body {
  background: var(--bg);
  color: var(--fg);
}
