import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  rowInput = 3;
  colInput = 4;

  rows: number[] = [];
  cols: number[] = [];

  constructor() {
    this.updateTable();
  }

  private normalizeCount(raw: number): number {
    if (!Number.isFinite(raw)) {
      return 0;
    }
    const n = Math.floor(raw);
    const clamped = Math.max(0, Math.min(n, 20));
    return clamped;
  }

  updateTable(): void {
    const rows = this.normalizeCount(this.rowInput);
    const cols = this.normalizeCount(this.colInput);

    this.rows = Array.from({ length: rows }, (_, i) => i);
    this.cols = Array.from({ length: cols }, (_, i) => i);
  }
}

