import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

type Cell = 'X' | 'O' | null;

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  board: Cell[] = Array.from({ length: 9 }, () => null);
  currentPlayer: 'X' | 'O' = 'X';
  winner: 'X' | 'O' | null = null;

  get isDraw(): boolean {
    return !this.winner && this.board.every((c) => c !== null);
  }

  get isGameOver(): boolean {
    return !!this.winner || this.isDraw;
  }

  get status(): string {
    if (this.winner) return `Winner: ${this.winner}`;
    if (this.isDraw) return 'Draw';
    return `Next: ${this.currentPlayer}`;
  }

  play(i: number): void {
    if (this.isGameOver) return;
    if (this.board[i] !== null) return;

    const next = this.board.slice();
    next[i] = this.currentPlayer;
    this.board = next;

    const w = this.calculateWinner(this.board);
    if (w) {
      this.winner = w;
      return;
    }

    if (!this.isDraw) {
      this.currentPlayer = this.currentPlayer === 'X' ? 'O' : 'X';
    }
  }

  reset(): void {
    this.board = Array.from({ length: 9 }, () => null);
    this.currentPlayer = 'X';
    this.winner = null;
  }

  private calculateWinner(b: Cell[]): 'X' | 'O' | null {
    const lines: number[][] = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];

    for (const [a, c, d] of lines) {
      const v = b[a];
      if (v && v === b[c] && v === b[d]) return v;
    }

    return null;
  }
}

