  // TODO: derived class based on thresholds
  // Example thresholds:
  // - < 34  => 'bar red'
  // - 34-66 => 'bar orange'
  // - > 66  => 'bar green'
  const barClass = useMemo(() => {
    return 'bar red';
  }, [progress]);

  // TODO: increase by 10 (clamp to 100)
  const inc = () => {};

  // TODO: decrease by 10 (clamp to 0)
  const dec = () => {};

  return (
    <main className="page">
      <section className="card">
        <h1 className="title">Progress Bar</h1>
        <p className="subtitle">
