import React, { useMemo, useState } from 'react';
import './App.css';

const MIN_SIZE = 2;
const MAX_SIZE = 20;

function clampSize(value: number): number {
  if (!Number.isFinite(value)) return MIN_SIZE;
  return Math.max(MIN_SIZE, Math.min(MAX_SIZE, Math.trunc(value)));
}

function toKey(row: number, col: number): string {
  return `${row}-${col}`;
}

export default function App() {
  const [size, setSize] = useState(8);
  const [selectedKey, setSelectedKey] = useState<string | null>(null);

  function onSizeChange(raw: string) {
    const next = clampSize(Number(raw));
    setSize(next);
    setSelectedKey(null);
  }

  function onSelectCell(row: number, col: number) {
    setSelectedKey(toKey(row, col));
  }

  const rows = useMemo(() => Array.from({ length: size }, (_, row) => row), [size]);
  const cols = useMemo(() => Array.from({ length: size }, (_, col) => col), [size]);

  const selectedLabel = selectedKey
    ? (() => {
        const [row, col] = selectedKey.split('-').map(Number);
        return `Selected: row ${row + 1}, column ${col + 1}`;
      })()
    : 'Selected: none';

  return (
    <main className="page">
      <section className="card">
        <h1>Chessboard Click / Highlight</h1>
        <p className="subtitle">
          Build an N×N chessboard where clicking one cell highlights it and clears previous selection.
        </p>

        <label className="size" htmlFor="board-size">
          Board size (N)
          <input
            id="board-size"
            type="number"
            min={MIN_SIZE}
            max={MAX_SIZE}
            value={size}
            onChange={(e) => onSizeChange(e.target.value)}
          />
        </label>

        <p className="status" aria-live="polite">{selectedLabel}</p>

        <div
          className="board"
          role="grid"
          aria-label="interactive chessboard"
          style={{ gridTemplateColumns: `repeat(${size}, minmax(0, 1fr))` }}
        >
          {rows.map((row) =>
            cols.map((col) => {
              const isDark = (row + col) % 2 === 1;
              const key = toKey(row, col);
              const isActive = selectedKey === key;

              return (
                <button
                  key={key}
                  type="button"
                  role="gridcell"
                  className={`cell ${isDark ? 'cell--dark' : 'cell--light'} ${isActive ? 'cell--active' : ''}`}
                  onClick={() => onSelectCell(row, col)}
                  aria-label={`Row ${row + 1} Column ${col + 1}`}
                  aria-pressed={isActive}
                />
              );
            })
          )}
        </div>
      </section>
    </main>
  );
}

