import React, { useState } from 'react';
import './App.css';

type Role = 'admin' | 'editor' | 'viewer';
type RoleFilter = 'all' | Role;

interface User {
  id: number;
  name: string;
  role: Role;
  active: boolean;
}

const USERS: User[] = [
  { id: 1, name: 'Alice Johnson', role: 'admin', active: true },
  { id: 2, name: 'Bob Smith', role: 'editor', active: false },
  { id: 3, name: 'Charlie Kim', role: 'viewer', active: true },
  { id: 4, name: 'Dana Patel', role: 'editor', active: true },
  { id: 5, name: 'Elliot Brown', role: 'viewer', active: false }
];

export default function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRole, setSelectedRole] = useState('all' as RoleFilter);
  const [showOnlyActive, setShowOnlyActive] = useState(false);

  const filteredUsers = USERS.filter((user) => {
    const term = searchTerm.trim().toLowerCase();

    if (term && !user.name.toLowerCase().includes(term)) {
      return false;
    }

    if (selectedRole !== 'all' && user.role !== selectedRole) {
      return false;
    }

    if (showOnlyActive && !user.active) {
      return false;
    }

    return true;
  });

  return (
    <main className="shell">
      <section className="card">
        <h1>Filterable User List (Solution)</h1>
        <p className="subtitle">
          Search by name, filter by role, and show only active users.
        </p>

        <div className="controls">
          <div className="field">
            <label htmlFor="search">Search</label>
            <input
              id="search"
              type="text"
              placeholder="Search by name..."
              value={searchTerm}
              onChange={(event) => setSearchTerm(event.target.value)}
            />
          </div>

          <div className="field">
            <label htmlFor="role">Role</label>
            <select
              id="role"
              value={selectedRole}
              onChange={(event) => setSelectedRole(event.target.value as RoleFilter)}
            >
              <option value="all">All roles</option>
              <option value="admin">Admin</option>
              <option value="editor">Editor</option>
              <option value="viewer">Viewer</option>
            </select>
          </div>

          <label className="checkbox-field">
            <input
              type="checkbox"
              checked={showOnlyActive}
              onChange={(event) => setShowOnlyActive(event.target.checked)}
            />
            <span>Only active users</span>
          </label>
        </div>

        <ul className="list" aria-label="Filtered users">
          {filteredUsers.map((user) => (
            <li key={user.id} className="row">
              <div className="primary">{user.name}</div>
              <div className="meta">
                <span className="pill">{user.role.charAt(0).toUpperCase() + user.role.slice(1)}</span>
                <span className={user.active ? 'status' : 'status inactive'}>
                  {user.active ? 'Active' : 'Inactive'}
                </span>
              </div>
            </li>
          ))}

          {filteredUsers.length === 0 && (
            <li className="empty">No users match your filters.</li>
          )}
        </ul>
      </section>
    </main>
  );
}

