:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.shell { min-height:100vh; display:grid; place-items:center; background:#020617; color:#e5e7eb; padding:24px; }
.card { width:100%; max-width:640px; background:#020617; border-radius:16px; border:1px solid #1f2937; padding:20px 20px 24px; box-shadow:0 18px 45px rgba(15,23,42,0.8); display:grid; gap:16px; }
.card h1 { margin:0; font-size:20px; font-weight:700; }
.subtitle { margin:0; font-size:13px; color:#9ca3af; }

.controls { display:grid; grid-template-columns:repeat(2,minmax(0,1fr)); gap:12px; align-items:end; margin-top:8px; }
.controls .field { display:grid; gap:4px; font-size:13px; }
.controls label { color:#d1d5db; }
.controls input[type="text"],
.controls select { border-radius:10px; border:1px solid #374151; background:#020617; padding:8px 10px; color:#e5e7eb; font-size:13px; }
.checkbox-field { display:flex; align-items:center; gap:6px; font-size:13px; color:#d1d5db; }
.checkbox-field input { width:14px; height:14px; }

.list { list-style:none; margin:12px 0 0; padding:0; border-radius:12px; border:1px solid #111827; background:radial-gradient(circle at top left,#111827,#020617); }
.row { display:flex; justify-content:space-between; align-items:center; padding:10px 14px; border-bottom:1px solid #111827; font-size:13px; }
.row:last-child { border-bottom:none; }
.primary { font-weight:600; color:#e5e7eb; }
.meta { display:flex; gap:8px; align-items:center; }
.pill { padding:2px 8px; border-radius:999px; border:1px solid #4b5563; font-size:11px; color:#e5e7eb; }
.status { font-size:12px; color:#22c55e; }
.status.inactive { color:#f97316; }
.empty { padding:10px 14px; font-size:13px; color:#9ca3af; text-align:center; }

@media (max-width:600px) {
  .controls { grid-template-columns:1fr; }
}
