import React, { useCallback, useState } from 'react';
import './App.css';

export default function App() {
  const [liked, setLiked] = useState(false);
  const [count, setCount] = useState(120);

  const toggleLike = useCallback(() => {
    setLiked((prevLiked) => {
      setCount((prevCount) => {
        if (!prevLiked) return prevCount + 1; // liking
        return Math.max(0, prevCount - 1); // unliking
      });
      return !prevLiked;
    });
  }, []);

  return (
    <main className="page">
      <section className="card">
        <h1 className="title">Like Button</h1>
        <p className="subtitle">Build a Facebook-style like button that toggles state and updates a counter.</p>

        <div className="row">
          <button
            className={"likeBtn" + (liked ? " active" : "")}
            onClick={toggleLike}
            aria-pressed={liked}
            aria-label="like button"
          >
            <span className="icon" aria-hidden="true">üëç</span>
            <span className="label">{liked ? 'Liked' : 'Like'}</span>
          </button>

          <div className="count" aria-label="like count">{count}</div>
        </div>

        <p className="hint">Click toggles state. Count updates +1 / -1 and never goes below 0.</p>
      </section>
    </main>
  );
}
