import React, { useState } from 'react';
import './App.css';

type User = {
  id: number;
  name: string;
  email: string;
  role: string;
};

const USERS: User[] = [
  { id: 1, name: 'Alice Johnson', email: 'alice@example.com', role: 'Admin' },
  { id: 2, name: 'Bob Smith', email: 'bob@example.com', role: 'Editor' },
  { id: 3, name: 'Carla Perez', email: 'carla@example.com', role: 'Viewer' },
  { id: 4, name: 'David Kim', email: 'david@example.com', role: 'Editor' },
  { id: 5, name: 'Emma Brown', email: 'emma@example.com', role: 'Viewer' },
  { id: 6, name: 'Frank Wu', email: 'frank@example.com', role: 'Admin' },
  { id: 7, name: 'Grace Lee', email: 'grace@example.com', role: 'Viewer' },
  { id: 8, name: 'Henry Ford', email: 'henry@example.com', role: 'Editor' },
  { id: 9, name: 'Ivy Stone', email: 'ivy@example.com', role: 'Viewer' },
  { id: 10, name: 'Jack Ryan', email: 'jack@example.com', role: 'Viewer' },
  { id: 11, name: 'Karen Diaz', email: 'karen@example.com', role: 'Editor' },
  { id: 12, name: 'Leo Walker', email: 'leo@example.com', role: 'Viewer' },
  { id: 13, name: 'Mia Clark', email: 'mia@example.com', role: 'Admin' },
  { id: 14, name: 'Noah Hall', email: 'noah@example.com', role: 'Viewer' },
  { id: 15, name: 'Olivia King', email: 'olivia@example.com', role: 'Editor' },
  { id: 16, name: 'Paul Green', email: 'paul@example.com', role: 'Viewer' },
  { id: 17, name: 'Quinn Ross', email: 'quinn@example.com', role: 'Viewer' },
  { id: 18, name: 'Rita Long', email: 'rita@example.com', role: 'Editor' },
  { id: 19, name: 'Sam Young', email: 'sam@example.com', role: 'Viewer' },
  { id: 20, name: 'Tina Fox', email: 'tina@example.com', role: 'Viewer' }
];

const PAGE_SIZE = 5;

export default function App() {
  const [currentPage, setCurrentPage] = useState(0);

  const totalPages = Math.ceil(USERS.length / PAGE_SIZE) || 1;

  const start = currentPage * PAGE_SIZE;
  const end = start + PAGE_SIZE;
  const pagedUsers: User[] = USERS.slice(start, end);

  const isFirstPage = currentPage === 0;
  const isLastPage = currentPage >= totalPages - 1;

  const goPrevious = () => {
    setCurrentPage((page) => (page > 0 ? page - 1 : page));
  };

  const goNext = () => {
    setCurrentPage((page) => (page < totalPages - 1 ? page + 1 : page));
  };

  return (
    <main className="main">
      <section className="section">
        <header className="header">
          <div>
            <h1>Users (Solution)</h1>
            <p className="subtitle">Simple paginated table (5 per page)</p>
          </div>
          <div className="meta">Total users: {USERS.length}</div>
        </header>

        <div className="panel">
          <table className="table" aria-label="Users table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Email</th>
                <th>Role</th>
              </tr>
            </thead>
            <tbody>
              {pagedUsers.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <footer className="footer">
            <button
              type="button"
              className="ghost"
              onClick={goPrevious}
              disabled={isFirstPage}
            >
              Previous
            </button>
            <span className="page-info">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              type="button"
              onClick={goNext}
              disabled={isLastPage}
            >
              Next
            </button>
          </footer>
        </div>
      </section>
    </main>
  );
}

