:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#020617; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:720px; background:#020617; border-radius:16px; border:1px solid #1f2937; padding:20px; box-sizing:border-box; display:grid; gap:12px; }

.header { display:flex; align-items:flex-end; justify-content:space-between; gap:12px; }
.header h1 { margin:0; font-size:20px; font-weight:700; }
.subtitle { margin:2px 0 0; font-size:12px; color:#9ca3af; }
.meta { font-size:12px; color:#9ca3af; }

.panel { background:#020617; border-radius:12px; border:1px solid #1f2937; overflow:hidden; }

.table { width:100%; border-collapse:collapse; font-size:13px; }
.table thead { background:#0b1020; }
.table th, .table td { padding:8px 10px; border-bottom:1px solid #111827; text-align:left; }
.table tbody tr:nth-child(even) { background:#020617; }

.footer { display:flex; align-items:center; justify-content:space-between; padding:10px 12px; gap:8px; }
.page-info { font-size:12px; color:#9ca3af; }

button { border:none; border-radius:999px; padding:6px 14px; font-size:13px; font-weight:600; cursor:pointer; background:#22c55e; color:#021210; }
button.ghost { background:transparent; color:#e5e7eb; border:1px solid #374151; }
button[disabled] { opacity:0.5; cursor:not-allowed; }
