import React, { useState } from 'react';
import './App.css';

type TabId = 'overview' | 'details' | 'settings';

export default function App() {
  const [activeTab, setActiveTab] = useState('overview' as TabId);

  const isActive = (tab: TabId) => activeTab === tab;

  const selectTab = (tab: TabId) => {
    setActiveTab(tab);
  };

  return (
    <main className="shell">
      <section className="card">
        <h1>Tabs / Multi-View Switcher (Solution)</h1>
        <p className="subtitle">Click the tabs to switch between different content panels.</p>

        <nav className="tabs" aria-label="Example tabs">
          <button className={isActive('overview') ? 'tab active' : 'tab'} onClick={() => selectTab('overview')}>Overview</button>
          <button className={isActive('details') ? 'tab active' : 'tab'} onClick={() => selectTab('details')}>Details</button>
          <button className={isActive('settings') ? 'tab active' : 'tab'} onClick={() => selectTab('settings')}>Settings</button>
        </nav>

        {isActive('overview') && (
          <section className="panel" aria-label="Overview content">
            <h2>Overview</h2>
            <p>Show a short summary or welcome message in this panel.</p>
          </section>
        )}

        {isActive('details') && (
          <section className="panel" aria-label="Details content">
            <h2>Details</h2>
            <p>Render some more detailed information here.</p>
          </section>
        )}

        {isActive('settings') && (
          <section className="panel" aria-label="Settings content">
            <h2>Settings</h2>
            <p>Display simple settings-like text or placeholders in this panel.</p>
          </section>
        )}
      </section>
    </main>
  );
}

