:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#020617; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.shell { min-height:100vh; display:grid; place-items:center; background:#020617; color:#e5e7eb; padding:24px; }
.card { width:100%; max-width:560px; background:#020617; border-radius:16px; border:1px solid #1f2937; padding:20px 20px 24px; box-shadow:0 18px 45px rgba(15,23,42,0.8); display:grid; gap:16px; }
.card h1 { margin:0; font-size:20px; font-weight:700; }
.subtitle { margin:0; font-size:13px; color:#9ca3af; }

.tabs { display:flex; gap:8px; padding:4px; background:#020617; border-radius:999px; border:1px solid #1f2937; margin-top:8px; }
.tab { flex:1; padding:8px 10px; border-radius:999px; border:none; background:transparent; color:#9ca3af; font-size:13px; font-weight:600; cursor:pointer; transition:background .15s,color .15s; }
.tab.active { background:#e5e7eb; color:#020617; }

.panel { margin-top:8px; border-radius:12px; border:1px solid #111827; background:radial-gradient(circle at top left,#111827,#020617); padding:14px 16px; display:grid; gap:4px; }
.panel h2 { margin:0 0 4px; font-size:16px; font-weight:600; }
.panel p { margin:0; font-size:13px; color:#d1d5db; line-height:1.5; }
