import React, { useMemo, useState } from 'react';
import './App.css';

type Cell = 'X' | 'O' | null;

const EMPTY_BOARD: Cell[] = Array.from({ length: 9 }, () => null);

function calculateWinner(b: Cell[]): 'X' | 'O' | null {
  const lines: number[][] = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];

  for (const [a, c, d] of lines) {
    const v = b[a];
    if (v && v === b[c] && v === b[d]) return v;
  }

  return null;
}

export default function App() {
  const [board, setBoard] = useState<Cell[]>(EMPTY_BOARD);
  const [currentPlayer, setCurrentPlayer] = useState<'X' | 'O'>('X');
  const [winner, setWinner] = useState<'X' | 'O' | null>(null);

  const isDraw = useMemo(() => !winner && board.every((c) => c !== null), [board, winner]);
  const isGameOver = !!winner || isDraw;

  const status = useMemo(() => {
    if (winner) return `Winner: ${winner}`;
    if (isDraw) return 'Draw';
    return `Next: ${currentPlayer}`;
  }, [currentPlayer, isDraw, winner]);

  const play = (i: number) => {
    if (isGameOver) return;
    if (board[i] !== null) return;

    const next = board.slice();
    next[i] = currentPlayer;
    setBoard(next);

    const w = calculateWinner(next);
    if (w) {
      setWinner(w);
      return;
    }

    if (!next.every((c) => c !== null)) {
      setCurrentPlayer((p) => (p === 'X' ? 'O' : 'X'));
    }
  };

  const reset = () => {
    setBoard(Array.from({ length: 9 }, () => null));
    setCurrentPlayer('X');
    setWinner(null);
  };

  return (
    <main className="page">
      <section className="card">
        <h1 className="title">Tic-Tac-Toe</h1>
        <p className="subtitle">Two players take turns. First to 3 in a row wins.</p>

        <div className="status" aria-live="polite">{status}</div>

        <div className="board" role="grid" aria-label="tic tac toe board">
          {board.map((cell, i) => (
            <button
              key={i}
              className="cell"
              type="button"
              onClick={() => play(i)}
              disabled={isGameOver || cell !== null}
              aria-label={`cell ${i}`}
            >
              {cell ?? ''}
            </button>
          ))}
        </div>

        <div className="actions">
          <button className="ghost" type="button" onClick={reset}>
            Reset
          </button>
        </div>
      </section>
    </main>
  );
}
