import App from './App.vue';
import './App.css';

createApp(App).mount('#app');

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.page { min-height:100vh; display:grid; place-items:center; background:#0a0a0a; padding:24px; }
.card { width:100%; max-width:420px; background:#0b1020; border:1px solid #1f2937; border-radius:16px; padding:18px; display:grid; gap:14px; color:#e5e7eb; }
.title { margin:0; font-size:20px; font-weight:900; color:#e5e7eb; }
.subtitle { margin:0; color:#9ca3af; font-size:12px; line-height:1.45; }

.list { display:grid; gap:10px; }

.counterBtn { background:#0a0f1e; border:1px solid #1f2937; border-radius:12px; padding:12px 14px; font-weight:900; cursor:pointer; color:#e5e7eb; text-align:center; }
.counterBtn:hover { border-color:#374151; }

.hint { color:#9ca3af; font-size:12px; line-height:1.45; }
