import { createApp } from 'vue';
import App from './App.vue';
import './App.css';

createApp(App).mount('#app');

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:420px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }

.panel { background:#0b1020; border:1px solid #1f2937; border-radius:12px; padding:16px; display:grid; gap:12px; }
.hint { margin:0; font-size:14px; color:#9ca3af; }

.stars { display:inline-flex; gap:4px; }
.star { background:transparent; border:none; font-size:28px; cursor:pointer; padding:4px; line-height:1; color:#4b5563; transition:transform .1s ease,color .1s ease; }
.star.filled { color:#facc15; }
.star:focus-visible { outline:2px solid #facc15; outline-offset:2px; }
.star:hover { transform:scale(1.1); }
