files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:420px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }

.panel { background:#0b1020; border:1px solid #1f2937; border-radius:12px; padding:16px; }
.value { font-size:48px; font-weight:800; text-align:center; margin-bottom:12px; }
.actions { display:flex; gap:8px; justify-content:center; }

button { background:#a3e635; color:#111; border:none; border-radius:10px; padding:10px 14px; font-weight:700; cursor:pointer; }
button[disabled] { opacity:.5; cursor:not-allowed; }
button.ghost { background:transparent; color:#e5e7eb; border:1px solid #374151; }
