import { createApp } from 'vue';
import App from './App.vue';
import './App.css';

createApp(App).mount('#app');

files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.page { min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.card { width:100%; max-width:520px; background:#0b1020; border:1px solid #1f2937; border-radius:16px; padding:18px; display:grid; gap:12px; }
.title { margin:0; font-size:20px; font-weight:900; }
.subtitle { margin:0; color:#9ca3af; font-size:12px; line-height:1.45; }
.value { font-size:28px; font-weight:900; }

.track { height:16px; border-radius:999px; background:#0a0f1e; border:1px solid #1f2937; overflow:hidden; }
.bar { height:100%; border-radius:999px; transition:width 160ms ease; }
.bar.red { background:#ef4444; }
.bar.orange { background:#f59e0b; }
.bar.green { background:#22c55e; }

.actions { display:flex; gap:10px; flex-wrap:wrap; }
button { background:#a3e635; color:#111; border:none; border-radius:12px; padding:10px 14px; font-weight:900; cursor:pointer; }
button.ghost { background:transparent; color:#e5e7eb; border:1px solid #374151; }
.hint { color:#9ca3af; font-size:12px; line-height:1.45; }
