files.src/App.css:
:root { color-scheme: dark; }
html, body { margin: 0; height: 100%; background:#0a0a0a; font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; }

.main { min-height:100vh; background:#0a0a0a; color:#e5e7eb; display:grid; place-items:center; padding:24px; }
.section { width:100%; max-width:480px; display:grid; gap:16px; }
h1 { margin:0; font-size:20px; font-weight:700; }

.panel { background:#0b1020; border:1px solid #1f2937; border-radius:12px; padding:16px; display:grid; gap:12px; }
.input-row { display:flex; gap:8px; }
.input-row input { flex:1; background:#020617; border-radius:8px; border:1px solid #1f2937; padding:8px 10px; color:#e5e7eb; font-size:14px; }
.input-row input:focus { outline:none; border-color:#6366f1; box-shadow:0 0 0 1px #6366f1; }

.primary { background:#4ade80; color:#022c22; border:none; border-radius:999px; padding:8px 14px; font-weight:600; font-size:14px; cursor:pointer; }
.primary[disabled] { opacity:.6; cursor:not-allowed; }

.list { list-style:none; margin:0; padding:0; display:grid; gap:6px; }
.item { display:flex; align-items:center; justify-content:space-between; gap:8px; padding:6px 8px; border-radius:8px; background:#020617; border:1px solid transparent; }
.item-main { display:flex; align-items:center; gap:8px; font-size:14px; }
.item-main input[type=checkbox] { accent-color:#22c55e; }
.item-main span.done { text-decoration:line-through; color:#9ca3af; }

button.ghost { background:transparent; color:#e5e7eb; border:1px solid #374151; border-radius:999px; padding:6px 10px; font-size:12px; cursor:pointer; }
button.ghost.small { padding:4px 10px; font-size:12px; }

.empty { margin:4px 0 0; font-size:13px; color:#9ca3af; text-align:center; }
.footer { display:flex; justify-content:flex-end; margin-top:4px; }
