function cssValue(el, prop){ return getComputedStyle(el).getPropertyValue(prop); }
function pxNum(v){ const m=/([\d.]+)px/.exec(v||''); return m?parseFloat(m[1]):NaN; }

const box = document.querySelector('.box');
const chips = document.querySelector('.chips');
const tags = [...document.querySelectorAll('.tag')];

// 1) Centering of inline content via text-align
it('centers inline content using text-align', () => {
  const centerOnChips = cssValue(chips, 'text-align').trim() === 'center';
  const centerOnBox = cssValue(box, 'text-align').trim() === 'center';
  expect(centerOnChips || centerOnBox).toBe(true);
});

// 2) Tags are inline-block with correct padding and radius
it('styles .tag as inline-block chips with padding and radius', () => {
  const ok = tags.every(t => {
    const disp = cssValue(t, 'display').trim();
    const pt = pxNum(cssValue(t, 'padding-top'));
    const pb = pxNum(cssValue(t, 'padding-bottom'));
    const pl = pxNum(cssValue(t, 'padding-left'));
    const pr = pxNum(cssValue(t, 'padding-right'));
    const br = pxNum(cssValue(t, 'border-radius'));
    return disp === 'inline-block' && pt===8 && pb===8 && pl===12 && pr===12 && br===6;
  });
  expect(ok).toBe(true);
});

// 3) ~8px gaps via margin-right on non-last tags (tolerate tiny rounding)
it('keeps ~8px gaps between chips', () => {
  if(tags.length < 2){ expect(true).toBe(true); return; }
  const nonLast = tags.slice(0, -1);
  const gapsOK = nonLast.every(t => pxNum(cssValue(t, 'margin-right')) >= 7.5);
  expect(gapsOK).toBe(true);
});

