function hasReducedMotionDisableTransitions(){
  try{
    const sheets=[...document.styleSheets];
    for(const s of sheets){
      const rules=[...((s.cssRules)||[])];
      for(const r of rules){
        if(r.media && (r.media.mediaText||'').includes('prefers-reduced-motion')){
          const inner=[...((r.cssRules)||[])];
          const found=inner.some(ir=>/transition\s*:\s*none/i.test(ir.cssText) || /transition\s*:\s*0s/i.test(ir.cssText));
          if(found) return true;
        }
      }
    }
  }catch(e){}
  return false;
}

