/* Base page styles using variables (to be defined by you) */
html, body { height: 100%; }
body {
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  margin: 0;
  line-height: 1.6;
  background: var(--bg);   /* relies on your :root variables */
  color: var(--text);       /* relies on your :root variables */
  display: grid;
  place-items: center;
  padding: 2rem;
}

/* The component must use ONLY variables for colors */
.panel {
  background: var(--surface);
  color: var(--text);
  border: 1px solid var(--border, rgba(0,0,0,0.08));
  border-radius: 12px;
  padding: 1.25rem;
  box-shadow: 0 1px 2px rgba(0,0,0,.06);
  max-width: 520px;
}

.btn {
  background: var(--accent);
  color: #fff;
  border: 0;
  padding: .5rem .9rem;
  border-radius: .6rem;
  font-weight: 600;
}

web.tests:
function getAllRules(){ try { return [...document.styleSheets].flatMap(s=>[...((s.cssRules)||[])]);} catch(e){ return []; } }
function hasRootVars(){
  const rules=getAllRules();
  return rules.some(r=>r.selectorText==":root" && /--bg\s*:/.test(r.cssText) && /--text\s*:/.test(r.cssText));
}
function hasDarkMediaVars(){
  const rules=getAllRules();
  return rules.some(r=>r.media && (r.media.mediaText||'').includes('prefers-color-scheme: dark') && [...(r.cssRules||[])].some(ir=>ir.selectorText===':root' && /--bg\s*:/.test(ir.cssText)));
}
function ruleIndex(pred){ const rules=getAllRules(); for(let i=0;i<rules.length;i++){ const r=rules[i]; if(pred(r,i)) return i; } return -1; }
function darkMediaIndex(){ return ruleIndex(r=>r.media && (r.media.mediaText||'').includes('prefers-color-scheme: dark')); }
function themeDarkIndex(){ return ruleIndex(r=>r.selectorText && /(^|\s|\.)theme-dark/.test(r.selectorText)); }

it('defines core variables on :root',()=>{
  expect(hasRootVars()).toBe(true);
});

it('provides dark-mode variables via media query',()=>{
  expect(hasDarkMediaVars()).toBe(true);
});

it('declares .theme-dark override after the media query',()=>{
  const dmi=darkMediaIndex();
  const tdi=themeDarkIndex();
  expect(dmi>=0 && tdi>dmi).toBe(true);
});

