web.starterCss:
body {
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  margin: 2rem;
  line-height: 1.6;
}

web.tests:
it('contains <!DOCTYPE html> declaration', () => {
  const docType = document.doctype;
  expect(docType && docType.name.toLowerCase()).toBe('html');
});

it('has a <title> element inside <head>', () => {
  const t = document.querySelector('head title');
  expect(!!t && t.textContent.trim().length > 0).toBe(true);
});

it('has a single <h1> inside <body>', () => {
  const h1 = document.querySelectorAll('body h1');
  expect(h1.length).toBe(1);
});

it('has at least one <p> element after the <h1>', () => {
  const h1 = document.querySelector('h1');
  const p = document.querySelector('p');
  expect(!!p && h1.compareDocumentPosition(p) & Node.DOCUMENT_POSITION_FOLLOWING).toBeTruthy();
});
