web.starterCss:
body {
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  margin: 2rem;
  line-height: 1.6;
}

web.tests:
it('contains <!DOCTYPE html> declaration', () => {
  const docType = document.doctype;
  expect(docType && docType.name.toLowerCase()).toBe('html');
});

it('has a <title> element inside <head>', () => {
  const t = document.querySelector('head title');
  expect(!!t && t.textContent.trim().length > 0).toBe(true);
});

it('has a single <h1> inside <body>', () => {
  const h1 = document.querySelectorAll('body h1');
  expect(h1.length).toBe(1);
});

it('has at least one <p> element after the <h1>', () => {
  const h1 = document.querySelector('h1');
  const p = document.querySelector('p');
  expect(!!p && h1.compareDocumentPosition(p) & Node.DOCUMENT_POSITION_FOLLOWING).toBeTruthy();
});

web.starterCss:
body {
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  margin: 2rem;
  line-height: 1.6;
}
img {
  vertical-align: middle;
}

web.tests:
it('has at least two <p> elements', () => {
  const ps = document.querySelectorAll('p');
  expect(ps.length >= 2).toBe(true);
});

it('has an <img> element present', () => {
  const im = document.querySelector('img');
  expect(!!im).toBeTruthy();
});

it('has a <strong> inside one of the paragraphs', () => {
  const strongInP = document.querySelector('p strong');
  expect(!!strongInP).toBeTruthy();
});

it('computed display defaults match: <p> is block, <img> is inline, <strong> is inline', () => {
  const p = document.querySelector('p');
  const img = document.querySelector('img');
  const strong = document.querySelector('strong');
  const gp = window.getComputedStyle(p).display;
  const gi = window.getComputedStyle(img).display;
  const gs = window.getComputedStyle(strong).display;
  expect(gp).toBe('block');
  expect(gi).toBe('inline');
  expect(gs).toBe('inline');
});

it('has an explanation section that references inline vs block for p, img, and strong', () => {
  const exp = document.querySelector('#explanation');
  expect(!!exp).toBeTruthy();
  const t = (exp.textContent || '').toLowerCase();
  expect(t.includes('inline')).toBeTruthy();
  expect(t.includes('block')).toBeTruthy();
  expect(t.includes('p')).toBeTruthy();
  expect(t.includes('img')).toBeTruthy();
  expect(t.includes('strong')).toBeTruthy();
});

web.starterCss:
body {
  font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
  margin: 2rem;
  line-height: 1.6;
  text-align: center;
}
img {
  width: 150px;
  height: auto;
  border: 2px solid #ccc;
  border-radius: 6px;
  margin-top: 1rem;
}
h1 {
  font-size: 1.5rem;
  margin-bottom: .5rem;
}
.note {
  font-size: .9rem;
  opacity: .75;
  margin-top: 1rem;
}

web.tests:
it('has an <a> element with an href attribute', () => {
  const a = document.querySelector('a');
  expect(a && a.hasAttribute('href')).toBe(true);
});

it('has target="_blank" on the <a> element', () => {
  const a = document.querySelector('a');
  expect(a && a.getAttribute('target')).toBe('_blank');
});

it('has rel="noopener noreferrer" on the <a> element', () => {
  const a = document.querySelector('a');
  const rel = a ? a.getAttribute('rel') : '';
  expect(rel && rel.includes('noopener') && rel.includes('noreferrer')).toBe(true);
});

it('has an <img> element inside the <a> element', () => {
  const img = document.querySelector('a img');
  expect(!!img).toBe(true);
});

it('the <img> has a non-empty alt attribute', () => {
  const img = document.querySelector('a img');
  const alt = img ? img.getAttribute('alt') : '';
  expect(alt && alt.trim().length > 0).toBe(true);
});

it('does not require real navigation in sandboxed previews (structural only)', () => {
  const a = document.querySelector('a');
  // No click/navigation expectation here by design
  expect(!!a).toBe(true);
});

