body { font-family: system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif; margin: 2rem; }
nav ul { list-style: none; padding: 0; display: flex; gap: 1rem; }
nav a { text-decoration: none; color: #007acc; }
nav a:hover { text-decoration: underline; }
section { margin-top: 1rem; }

web.tests:
it('has a <nav> containing a <ul> with three <li> links', () => {
  const lis = document.querySelectorAll('nav ul li');
  expect(lis.length).toBe(3);
});

it('has exactly three <a> elements and each links to an internal section', () => {
  const links = document.querySelectorAll('nav a');
  expect(links.length).toBe(3);
  const allInternal = Array.from(links).every(a => {
    const href = a.getAttribute('href') || '';
    return href.startsWith('#') && href.length > 1;
  });
  expect(allInternal).toBe(true);
});

it('has sections with matching IDs: home, about, contact', () => {
  ['home', 'about', 'contact'].forEach(id => {
    const sec = document.getElementById(id);
    expect(!!sec).toBe(true);
  });
});

it('shows only #home by default', () => {
  const sections = Array.from(document.querySelectorAll('section'));
  const visible = sections.filter(s => !s.hasAttribute('hidden'));
  expect(visible.length).toBe(1);
  expect(visible[0].id).toBe('home');
});

