starterCodeTs:
export default function unique<T>(arr: T[]): T[] {
  // TODO: Return new array with duplicates removed
  throw new Error('Not implemented');
}

solutionBlock.codeJs:
export default function unique(arr) {
  const seen = new Set();
  const result = [];
  for (const item of arr) {
    if (!seen.has(item)) {
      seen.add(item);
      result.push(item);
    }
  }
  return result;
}

solutionBlock.codeTs:
export default function unique<T>(arr: T[]): T[] {
  const seen = new Set<T>();
  const result: T[] = [];
  for (const item of arr) {
    if (!seen.has(item)) {
      seen.add(item);
      result.push(item);
    }
  }
  return result;
}

tests:
