solutionBlock.codeJs:
export default function capitalizeWords(sentence) {
  if (!sentence) return '';
  return sentence
    .split(' ')
    .map(word => {
      if (!word) return word;
      return word[0].toUpperCase() + word.slice(1).toLowerCase();
    })
    .join(' ');
}

solutionBlock.codeTs:
