solutionBlock.codeJs:
export default function throttle(fn, interval) {
  let lastTime = 0;
  return function(...args) {
    const now = Date.now();
    if (now - lastTime >= interval) {
      fn.apply(this, args);
      lastTime = now;
    }
  };
}

solutionBlock.codeTs:
export default function throttle<F extends (...a: any[]) => void>(fn: F, interval: number): F {
  let lastTime = 0;
  return function(this: any, ...args: Parameters<F>) {
    const now = Date.now();
    if (now - lastTime >= interval) {
      fn.apply(this, args);
      lastTime = now;
    }
  } as F;
}

