import deepClone from './deepClone';

describe('deepClone', () => {
  test('clones nested objects', () => {
    const obj = { a: 1, b: { c: 2 } };
    const clone = deepClone(obj);
    expect(clone).toEqual(obj);
    clone.b.c = 5;
    expect(obj.b.c).toBe(2);
  });

  test('clones arrays correctly', () => {
    const arr = [1, { x: 2 }, [3, 4]];
    const clone = deepClone(arr);
    expect(clone).toEqual(arr);
    clone[1].x = 99;
    expect(arr[1].x).toBe(2);
  });

  test('clones Date and RegExp instances', () => {
    const obj = { d: new Date('2020-01-01T00:00:00Z'), r: /abc/i };
    const clone = deepClone(obj);

    // Date
    expect(clone.d instanceof Date).toBe(true);
    expect(clone.d === obj.d).toBe(false);   // instead of .not.toBe
    expect(clone.d.getTime()).toBe(obj.d.getTime());

    // RegExp (single guarded assertion)
    const sameRegExp = (
      clone.r instanceof RegExp &&
      clone.r !== obj.r &&                       // avoids .not
      clone.r.source === obj.r.source &&
      (('flags' in clone.r && 'flags' in obj.r)
        ? clone.r.flags === obj.r.flags
        : String(clone.r) === String(obj.r)) &&
      clone.r.lastIndex === obj.r.lastIndex
    );
    expect(sameRegExp).toBe(true);
  });

  test('returns primitives as-is', () => {
    expect(deepClone(5)).toBe(5);
    expect(deepClone('test')).toBe('test');
    expect(deepClone(null)).toBe(null);
  });
});

