import memoize from './memoize';

describe('memoize', () => {
  test('caches results for repeated calls', () => {
    let calls = 0;
    const add = (a, b) => { calls++; return a + b; };
    const memoAdd = memoize(add);
    expect(memoAdd(1, 2)).toBe(3);
    expect(memoAdd(1, 2)).toBe(3);
    expect(calls).toBe(1);
  });

  test('handles different arguments separately', () => {
    let calls = 0;
    const multiply = (a, b) => { calls++; return a * b; };
    const memoMult = memoize(multiply);
    memoMult(2, 3);
    memoMult(3, 4);
    expect(calls).toBe(2);
  });

  test('works with multiple argument calls', () => {
    const join = (...args) => args.join('-');
    const memoJoin = memoize(join);
    expect(memoJoin('a', 'b', 'c')).toBe('a-b-c');
    expect(memoJoin('a', 'b', 'c')).toBe('a-b-c');
  });
});

