  it('caches computed results', () => {
    let count = 0;
    const add = (a: number, b: number) => { count++; return a + b; };
    const memoAdd = memoize(add);
    expect(memoAdd(3, 4)).toBe(7);
    expect(memoAdd(3, 4)).toBe(7);
    expect(count).toBe(1);
  });

  it('treats unique argument sets separately', () => {
    let count = 0;
    const diff = (a: number, b: number) => { count++; return a - b; };
    const memoDiff = memoize(diff);
    memoDiff(10, 5);
    memoDiff(8, 3);
    expect(count).toBe(2);
  });

  it('works for multi-arg functions', () => {
    const concat = (...args: string[]) => args.join('_');
    const memoConcat = memoize(concat);
    expect(memoConcat('a', 'b', 'c')).toBe('a_b_c');
    expect(memoConcat('a', 'b', 'c')).toBe('a_b_c');
  });
});

