  const double = n => n * 2;
  const subtract10 = n => n - 10;

  test('applies functions right-to-left', () => {
    const fn = compose(subtract10, double, add1);
    expect(fn(3)).toBe(-2);
  });

  test('works with single function', () => {
    const fn = compose(add1);
    expect(fn(4)).toBe(5);
