import groupBy from './groupBy';

describe('groupBy', () => {
  test('groups numbers by Math.floor', () => {
    const result = groupBy([6.1, 4.2, 6.3], Math.floor);
    expect(result).toEqual({ 4: [4.2], 6: [6.1, 6.3] });
  });

  test('groups strings by length', () => {
    const result = groupBy(['one', 'two', 'three'], str => str.length);
    expect(result).toEqual({ 3: ['one', 'two'], 5: ['three'] });
  });

  test('groups objects by property', () => {
    const data = [{ type: 'a' }, { type: 'b' }, { type: 'a' }];
    const result = groupBy(data, x => x.type);
    expect(result).toEqual({ a: [{ type: 'a' }, { type: 'a' }], b: [{ type: 'b' }] });
  });

  test('returns empty object for empty array', () => {
    expect(groupBy([], Math.floor)).toEqual({});
  });
});

