starterCode:
export default function clamp(value, lower, upper) {
  throw new Error('Not implemented');
}

starterCodeTs:
export default function clamp(value: number, lower: number, upper: number): number {
  throw new Error('Not implemented');
}

solutionBlock.codeJs:
export default function clamp(value, lower, upper) {
  if (value < lower) return lower;
  if (value > upper) return upper;
  return value;
}

solutionBlock.codeTs:
export default function clamp(value: number, lower: number, upper: number): number {
  if (value < lower) return lower;
  if (value > upper) return upper;
  return value;
}

tests:
import clamp from './clamp';

describe('clamp', () => {
  test('within range', () => expect(clamp(3, 0, 5)).toBe(3));
  test('below lower', () => expect(clamp(-10, -3, 5)).toBe(-3));
  test('above upper', () => expect(clamp(10, -5, 5)).toBe(5));
});

testsTs:
import clamp from './clamp';

describe('clamp (TypeScript)', () => {
  it('should return the same value when within range', () => {
    expect(clamp(3, 0, 5)).toBe(3);
  });
  it('should clamp to lower bound when below range', () => {
    expect(clamp(-10, -3, 5)).toBe(-3);
  });
  it('should clamp to upper bound when above range', () => {
    expect(clamp(10, -5, 5)).toBe(5);
  });
});

