export default function reverseString(str) {
  // TODO: Implement without using Array.prototype.reverse()
  throw new Error('Not implemented');
}

starterCodeTs:
export default function reverseString(str: string): string {
  // TODO: Implement without using Array.prototype.reverse()
  throw new Error('Not implemented');
}

solutionBlock.codeJs:
export default function reverseString(str) {
  let out = '';
  for (let i = str.length - 1; i >= 0; i--) {
    out += str[i];
  }
  return out;
}

solutionBlock.codeTs:
export default function reverseString(str: string): string {
  let out = '';
  for (let i = str.length - 1; i >= 0; i--) {
    out += str[i];
  }
  return out;
}

tests:
import reverseString from './reverseString';

describe('reverseString', () => {
  test('reverses simple strings', () => {
    expect(reverseString('abc')).toBe('cba');
    expect(reverseString('hello')).toBe('olleh');
  });

  test('handles palindromes', () => {
    expect(reverseString('racecar')).toBe('racecar');
  });

  test('reverses with spaces and punctuation', () => {
    expect(reverseString('A man, a plan')).toBe('nalp a ,nam A');
  });

  test('handles empty and single character', () => {
    expect(reverseString('')).toBe('');
    expect(reverseString('x')).toBe('x');
  });
});

