solutionBlock.codeJs:
export default function flattenOnce(arr) {
  const result = [];
  for (const el of arr) {
    if (Array.isArray(el)) {
      result.push(...el);
    } else {
      result.push(el);
    }
  }
  return result;
}

solutionBlock.codeTs:
export default function flattenOnce(arr: any[]): any[] {
  const result: any[] = [];
  for (const el of arr) {
    if (Array.isArray(el)) {
      result.push(...el);
    } else {
      result.push(el);
    }
  }
  return result;
}

tests:
import flattenOnce from './flattenOnce';

describe('flattenOnce', () => {
  test('flattens a simple nested array', () => {
    expect(flattenOnce([1, [2, 3], [4, [5]]])).toEqual([1, 2, 3, 4, [5]]);
  });

  test('returns same array when already flat', () => {
    expect(flattenOnce([1, 2, 3])).toEqual([1, 2, 3]);
  });

  test('handles mixed array contents', () => {
    expect(flattenOnce([[1], 2, [3, [4]], 5])).toEqual([1, 2, 3, [4], 5]);
  });

  test('returns empty array for empty input', () => {
    expect(flattenOnce([])).toEqual([]);
  });
