starterCodeTs:
export default function maxChar(str: string): string {
  // TODO: Return the most frequent character in the string
  throw new Error('Not implemented');
}

solutionBlock.codeJs:
export default function maxChar(str) {
  if (!str) return '';
  const charCount = {};
  let max = 0;
  let maxChar = '';

  for (const ch of str) {
    charCount[ch] = (charCount[ch] || 0) + 1;
    if (charCount[ch] > max) {
      max = charCount[ch];
      maxChar = ch;
    }
  }

  return maxChar;
}

solutionBlock.codeTs:
export default function maxChar(str: string): string {
  if (!str) return '';
  const charCount: Record<string, number> = {};
  let max = 0;
  let maxChar = '';

  for (const ch of str) {
    charCount[ch] = (charCount[ch] || 0) + 1;
    if (charCount[ch] > max) {
      max = charCount[ch];
      maxChar = ch;
    }
  }

  return maxChar;
}

