export default function debounce(fn, delay) {
  // TODO: Return a debounced function that delays execution until delay ms have passed
  throw new Error('Not implemented');
}

starterCodeTs:
export default function debounce<F extends (...a: any[]) => void>(fn: F, delay: number): F {
  // TODO: Return a debounced function that delays execution until delay ms have passed
  throw new Error('Not implemented');
}

solutionBlock.codeJs:
export default function debounce(fn, delay) {
  let timeoutId;
  return function(...args) {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => fn.apply(this, args), delay);
  };
}

solutionBlock.codeTs:
export default function debounce<F extends (...a: any[]) => void>(fn: F, delay: number): F {
  let timeoutId: ReturnType<typeof setTimeout>;
  return function(this: any, ...args: Parameters<F>) {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => fn.apply(this, args), delay);
  } as F;
}

tests:
import debounce from './debounce';

const wait = (ms) => new Promise((r) => setTimeout(r, ms));

describe('debounce', () => {
  test('calls function after delay', async () => {
    let calls = 0;
    const debounced = debounce(() => { calls += 1; }, 120);

    debounced();
    expect(calls).toBe(0);

    await wait(140);
    expect(calls).toBe(1);
  });

  test('resets timer on rapid calls (only last executes)', async () => {
    let calls = 0;
    const debounced = debounce(() => { calls += 1; }, 100);

    debounced();
    await wait(50);
    debounced();
    await wait(50);
    debounced();

    await wait(90);
    expect(calls).toBe(0);

    await wait(20);
    expect(calls).toBe(1);
  });

  test('passes arguments (and preserves this via apply)', async () => {
    let last = '';
    const debounced = debounce(function (name) {
      last = `Hi ${name}`;
    }, 80);

    debounced.call({ tag: 'ctx' }, 'Alice');
    await wait(100);
    expect(last).toBe('Hi Alice');
  });
});

testsTs:
import debounce from './debounce';

const wait = (ms: number) => new Promise<void>((r) => setTimeout(r, ms));

describe('debounce (TypeScript)', () => {
  it('delays function execution', async () => {
    let calls = 0;
    const debounced = debounce(() => { calls += 1; }, 120);

    debounced();
    expect(calls).toBe(0);

    await wait(140);
    expect(calls).toBe(1);
  });

  it('restarts timer on multiple calls', async () => {
    let calls = 0;
    const debounced = debounce(() => { calls += 1; }, 100);

    debounced();
    await wait(50);
    debounced();
    await wait(50);
    debounced();

    await wait(90);
    expect(calls).toBe(0);

    await wait(20);
    expect(calls).toBe(1);
  });

  it('forwards parameters correctly', async () => {
    let captured = '';
    const debounced = debounce((msg: string) => { captured = msg; }, 80);

    debounced('Hello');
    await wait(100);
    expect(captured).toBe('Hello');
  });
});

