export default async function mapAsyncLimit(items, limit, worker, strategy = 'fail-fast') {
  // TODO:
  // 1) Validate inputs (limit >= 1)
  // 2) Launch up to `limit` concurrent runners
  // 3) Each runner pulls the next index and writes its result at that index
  // 4) 'fail-fast' rejects on first error, 'all-settled' aggregates statuses
}

export const sleep = (ms) => new Promise(r => setTimeout(r, ms));

solutionBlock.codeJs:
export default function mapAsyncLimit(items, limit, worker, strategy = 'fail-fast') {
  if (!Number.isFinite(limit) || limit < 1) throw new Error('limit must be >= 1');
  const n = items.length, allSettled = strategy === 'all-settled';
  const results = new Array(n);
  if (n === 0) return Promise.resolve(results);

  let next = 0, done = 0, failed = false;

  return new Promise((resolve, reject) => {
    const runner = async () => {
      while (true) {
        const i = next++;
        if (i >= n || (failed && !allSettled)) break;
        try {
          const v = await worker(items[i], i);
          results[i] = allSettled ? { status: 'fulfilled', value: v } : v;
        } catch (err) {
          if (allSettled) results[i] = { status: 'rejected', reason: err };
          else { failed = true; reject(err); return; }
        } finally {
          if (++done === n && (!failed || allSettled)) resolve(results);
        }
      }
    };

    const k = Math.min(limit, n);
    for (let j = 0; j < k; j++) runner();
  });
}

