import mapAsyncLimit from './mapAsyncLimit';

const sleep = (ms) => new Promise(r => setTimeout(r, ms));

describe('mapAsyncLimit', () => {
  test('preserves order with mixed durations', async () => {
    const items = [30, 10, 20];
    const out = await mapAsyncLimit(items, 2, async (ms) => { await sleep(ms); return ms / 10; });
    expect(out).toEqual([3, 1, 2]);
  });

  test('respects concurrency limit (no more than limit active)', async () => {
    const items = [30, 30, 30, 30, 30];
    let active = 0, maxActive = 0;
    const out = await mapAsyncLimit(items, 2, async (ms) => {
      active++; if (active > maxActive) maxActive = active;
      await sleep(ms);
      active--;
      return ms;
    });
    expect(out).toEqual(items);
    expect(maxActive <= 2).toEqual(true);
  });

  test('fail-fast rejects on first error', async () => {
    const items = [5, 10, 5];
    const start = Date.now();
    let threw = false;
    try {
      await mapAsyncLimit(items, 2, async (ms, idx) => {
        await sleep(ms);
        if (idx === 1) throw new Error('boom');
        return idx;
      }, 'fail-fast');
    } catch (e) {
      threw = true;
      expect(e && e.message).toEqual('boom');
    }
    expect(threw).toEqual(true);
    const elapsed = Date.now() - start;
    expect(elapsed < 1000).toEqual(true);
  });

  test('all-settled aggregates outcomes', async () => {
    const res = await mapAsyncLimit([10, 5, 10, 5], 2, async (ms, idx) => {
      await sleep(ms);
      if (idx % 2 === 1) throw new Error('odd');
      return idx * 10;
    }, 'all-settled');

    expect(res[0]).toEqual({ status: 'fulfilled', value: 0 });
    expect(res[1].status).toEqual('rejected');
    expect(!!(res[1].reason && res[1].reason.message)).toEqual(true);
    expect(res[2]).toEqual({ status: 'fulfilled', value: 20 });
    expect(res[3].status).toEqual('rejected');
  });

  test('throws if limit < 1', () => {
    let threw = false;
    try { mapAsyncLimit([1], 0, async (x) => x); } catch (e) { threw = true; }
    expect(threw).toEqual(true);
  });
});

