solutionBlock.codeJs:
function abortableHelpers() {}

// üí§ Small sleep helper for delaying async steps
abortableHelpers.sleep = (ms) => new Promise(r => setTimeout(r, ms));

// üïí 1. Race a promise against a timeout
abortableHelpers.withTimeout = function withTimeout(p, ms, reason) {
  return new Promise((resolve, reject) => {
    let settled = false; // prevents double resolution

    // Create timer that rejects if time runs out
    const id = setTimeout(() => {
      if (settled) return;
      settled = true;
      reject(reason ?? new Error('Timeout'));
    }, ms);

    // Wait for the input promise and clean up the timer afterward
    Promise.resolve(p).then(
      (v) => { if (!settled) { settled = true; clearTimeout(id); resolve(v); } },
      (e) => { if (!settled) { settled = true; clearTimeout(id); reject(e); } }
    );
  });
};

// üß© 2. Make any async task abortable
abortableHelpers.withAbort = function withAbort(factory, signal) {
  return new Promise((resolve, reject) => {
    // If the signal is already aborted, reject immediately
    if (signal?.aborted) { reject(signal.reason ?? new Error('Aborted')); return; }

    let finished = false;

    // Handle abort event (like pressing Cancel)
    const onAbort = () => {
      if (finished) return;
      finished = true;
      reject(signal.reason ?? new Error('Aborted'));
    };

    signal?.addEventListener('abort', onAbort, { once: true });

    // Run the async factory with access to the signal
    Promise.resolve()
      .then(() => factory(signal))
      .then((v) => {
        if (!finished) {
          finished = true;
          signal?.removeEventListener('abort', onAbort);
          resolve(v);
        }
      })
      .catch((e) => {
        if (!finished) {
          finished = true;
          signal?.removeEventListener('abort', onAbort);
          reject(e);
        }
      });
  });
};

// üîó 3. Merge multiple AbortSignals into one
abortableHelpers.composeAbort = function composeAbort(...signals) {
  const ac = new AbortController();
  if (!signals || signals.length === 0) return ac.signal; // nothing to combine

  // If any signal is already aborted, propagate immediately
  const already = signals.find(s => s?.aborted);
  if (already) { ac.abort(already.reason); return ac.signal; }

  const listeners = [];

  // When one signal aborts, abort the composed one and clean up all listeners
  const abortOnce = (reason) => {
    if (!ac.signal.aborted) ac.abort(reason);
    for (const { s, fn } of listeners) s.removeEventListener('abort', fn);
    listeners.length = 0;
  };

  // Attach listeners to all input signals
  for (const s of signals) {
    if (!s) continue;
    const fn = () => abortOnce(s.reason);
    s.addEventListener('abort', fn, { once: true });
    listeners.push({ s, fn });
  }

  return ac.signal;
};

export default abortableHelpers;

