function abortableHelpers() {}

abortableHelpers['sleep'] = (ms: number) => new Promise<void>(r => setTimeout(r, ms));

abortableHelpers['withTimeout'] = function withTimeout<T>(p: Promise<T>, ms: number, reason?: any): Promise<T> {
  return new Promise<T>((resolve, reject) => {
    let settled = false;
    const id = setTimeout(() => {
      if (settled) return;
      settled = true;
      reject(reason ?? new Error('Timeout'));
    }, ms);

    Promise.resolve(p).then(
      v => { if (!settled) { settled = true; clearTimeout(id); resolve(v); } },
      e => { if (!settled) { settled = true; clearTimeout(id); reject(e); } }
    );
  });
};

abortableHelpers['withAbort'] = function withAbort<T>(factory: (signal: AbortSignal) => Promise<T>, signal: AbortSignal): Promise<T> {
  return new Promise<T>((resolve, reject) => {
    if (signal?.aborted) { reject((signal as any).reason ?? new Error('Aborted')); return; }

    let finished = false;
    const onAbort = () => {
      if (finished) return;
      finished = true;
      reject((signal as any).reason ?? new Error('Aborted'));
    };

    signal?.addEventListener('abort', onAbort, { once: true });

    Promise.resolve()
      .then(() => factory(signal))
      .then(v => { if (!finished) { finished = true; signal?.removeEventListener('abort', onAbort); resolve(v); } })
      .catch(e => { if (!finished) { finished = true; signal?.removeEventListener('abort', onAbort); reject(e); } });
  });
};

abortableHelpers['composeAbort'] = function composeAbort(...signals: AbortSignal[]): AbortSignal {
  const ac = new AbortController();
  if (!signals || signals.length === 0) return ac.signal;

  const already = signals.find(s => s?.aborted);
  if (already) { ac.abort((already as any).reason); return ac.signal; }

  type L = { s: AbortSignal; fn: () => void };
  const listeners: L[] = [];
  const abortOnce = (reason: any) => {
    if (!ac.signal.aborted) ac.abort(reason);
    for (const { s, fn } of listeners) s.removeEventListener('abort', fn);
    listeners.length = 0;
  };

  for (const s of signals) {
    if (!s) continue;
    const fn = () => abortOnce((s as any).reason);
    s.addEventListener('abort', fn, { once: true });
    listeners.push({ s, fn });
  }

  return ac.signal;
};

export default abortableHelpers as unknown as {
  withTimeout: <T>(p: Promise<T>, ms: number, reason?: any) => Promise<T>,
  withAbort: <T>(factory: (signal: AbortSignal) => Promise<T>, signal: AbortSignal) => Promise<T>,
  composeAbort: (...signals: AbortSignal[]) => AbortSignal,
