    expect(threw).toEqual(true);
  });

  test('composeAbort aborts when any source aborts', async () => {
    const a = new AbortController();
    const b = new AbortController();
    const c = composeAbort(a.signal, b.signal);
    expect(c.aborted).toEqual(false);

