import helpers from './abortable-helpers';
const { withTimeout, withAbort, composeAbort, sleep } = helpers as unknown as {
  withTimeout: <T>(p: Promise<T>, ms: number, reason?: any) => Promise<T>,
  withAbort: <T>(factory: (signal: AbortSignal) => Promise<T>, signal: AbortSignal) => Promise<T>,
  composeAbort: (...signals: AbortSignal[]) => AbortSignal,
  sleep: (ms: number) => Promise<void>
};

function _sleep(ms: number){ return new Promise<void>(r=>setTimeout(r, ms)); }
const nap = typeof sleep === 'function' ? sleep : _sleep;

describe('Abortable Helpers (TS)', () => {
  test('withTimeout resolves fast', async () => {
    const res = await withTimeout(Promise.resolve(5), 100);
    expect(res).toEqual(5);
  });

  test('withTimeout rejects on timeout', async () => {
    let threw = false;
    try { await withTimeout(nap(200).then(()=>42), 10, new Error('Timeout')); }
    catch { threw = true; }
    expect(threw).toEqual(true);
  });

  test('withAbort resolves when not aborted', async () => {
    const ac = new AbortController();
    const v = await withAbort(async (signal) => { await nap(15); return signal.aborted ? -1 : 9; }, ac.signal);
    expect(v).toEqual(9);
  });

  test('withAbort rejects when aborted', async () => {
    const ac = new AbortController();
    let threw = false;
    const p = withAbort(async () => { await nap(100); return 1; }, ac.signal);
    setTimeout(()=>ac.abort(new Error('cancel')), 10);
    try { await p; } catch { threw = true; }
    expect(threw).toEqual(true);
  });

  test('composeAbort aborts if any input aborts', async () => {
    const a = new AbortController();
    const b = new AbortController();
    const sig = composeAbort(a.signal, b.signal);
    let threw = false;
    const p = withAbort(async () => { await nap(100); return 'ok'; }, sig);
    setTimeout(()=>a.abort(new Error('stop')), 10);
    try { await p; } catch { threw = true; }
    expect(sig.aborted).toEqual(true);
    expect(threw).toEqual(true);
  });

  test('composeAbort reflects already-aborted inputs', () => {
    const a = new AbortController(); a.abort(new Error('early'));
    const sig = composeAbort(a.signal);
    expect(sig.aborted).toEqual(true);
  });
});

