solutionBlock.codeJs:
/*
 * Streaming NDJSON parser
 * - Efficient: processes data chunk-by-chunk (low memory) and preserves order
 * - Robust: isolates malformed lines via onError callback and continues
 * - Correct decoding: uses TextDecoder(stream:true) for UTF-8 boundaries
 */
export default async function parseNDJSON(stream, onError) {
  if (!stream || typeof stream.getReader !== 'function') {
    throw new Error('parseNDJSON: expected a ReadableStream<Uint8Array>');
  }

  const reader = stream.getReader();          // Pull chunks respecting backpressure
  const decoder = new TextDecoder('utf-8');   // UTF-8 by default
  let buffer = '';                            // Carries partial line across chunks
  const out = [];                              // Collected parsed objects

  // Helper: parse complete lines inside `buffer` except the last partial one
  const drainLines = () => {
    // Split into lines; keep the last part (may be partial) in `buffer`
    const parts = buffer.split('\n');
    buffer = parts.pop() ?? '';

    for (let line of parts) {
      // Trim removes Windows CR ("\r\n") and blank lines
      line = line.trim();
      if (line === '') continue; // skip empty lines safely
      try {
        out.push(JSON.parse(line));
      } catch (err) {
        // Report and continue parsing subsequent lines
        if (typeof onError === 'function') onError(line, err);
      }
    }
  };

  // Read loop: decode incrementally and process complete lines as they appear
  while (true) {
    const { value, done } = await reader.read();
    if (done) break;
    // Append decoded text for this chunk; {stream:true} keeps decoder state for split code points
    buffer += decoder.decode(value, { stream: true });
    drainLines();
  }

  // Flush any remaining bytes from the decoder (e.g., last multi-byte char) and drain final lines
  buffer += decoder.decode();
  // Process any complete lines now in buffer (leaves potential final partial in buffer)
  drainLines();

  // If there's a final non-empty line without trailing "\n", attempt to parse it
  const tail = buffer.trim();
  if (tail !== '') {
    try {
      out.push(JSON.parse(tail));
    } catch (err) {
      if (typeof onError === 'function') onError(tail, err);
    }
  }

  // Release reader lock (optional but polite)
  try { reader.releaseLock(); } catch (_) {}

  return out;
}

// CommonJS fallback for environments that require it
try { if (typeof module !== 'undefined') module.exports = parseNDJSON; } catch (_) {}

