/*
 * Streaming NDJSON parser (TypeScript)
 * Same logic, with typed signatures for clarity.
 */
export default async function parseNDJSON(
  stream: ReadableStream<Uint8Array>,
  onError?: (line: string, err: any) => void
): Promise<any[]> {
  if (!stream || typeof (stream as any).getReader !== 'function') {
    throw new Error('parseNDJSON: expected a ReadableStream<Uint8Array>');
  }

  const reader = stream.getReader();
  const decoder = new TextDecoder('utf-8');
  let buffer = '';
  const out: any[] = [];

  const drainLines = () => {
    const parts = buffer.split('\n');
    buffer = parts.pop() ?? '';
    for (let raw of parts) {
      const line = raw.trim();
      if (line === '') continue;
      try { out.push(JSON.parse(line)); }
      catch (err) { if (onError) onError(line, err); }
    }
  };

  while (true) {
    const { value, done } = await reader.read();
    if (done) break;
    buffer += decoder.decode(value, { stream: true });
    drainLines();
  }

  buffer += decoder.decode();
  drainLines();
  const tail = buffer.trim();
  if (tail !== '') {
    try { out.push(JSON.parse(tail)); }
    catch (err) { if (onError) onError(tail, err); }
  }

  try { reader.releaseLock(); } catch {}
  return out;
}

// CommonJS fallback
// @ts-ignore
try { if (typeof module !== 'undefined') (module as any).exports = parseNDJSON; } catch {}

